#include<stdio.h>
#include<string.h>
#include<time.h>

int buscarX(char *s) {
    int i;
    for (i = 0; i < strlen(s); i++) {
        if (s[i] == 'X')
        return i;
    }
    return -1;
}

int buscarX2(char *s) {
    for(int i = 0; s[i] != '\0'; i++)
        if (s[i] == 'X')
            return i;
    
    return -1;
}

int main(void){
    char* str = "";
    clock_t start, end;
    double duration;
    int res1, res2;

    start = clock();
    res1 = buscarX(str);
    end = clock();
    duration = ((double) end - start)/CLOCKS_PER_SEC;
    printf("Duracion 1: %lf\n", duration);

    start = clock();
    res1 = buscarX2(str);
    end = clock();
    duration = ((double) end - start)/CLOCKS_PER_SEC;
    printf("Duracion 2: %lf\n", duration);

    return 0;
}